import * as React from "react";
import { SingleAction } from "./singleAction";
import { Droppable } from "react-beautiful-dnd";
import { Button } from "@mui/material";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import RefreshIcon from "@mui/icons-material/Refresh";
import AddBoxIcon from "@mui/icons-material/AddBox";
import DeleteIcon from "@mui/icons-material/Delete";
import DisabledByDefaultIcon from "@mui/icons-material/DisabledByDefault";
import { Sprites } from "./spriteProps";
import Positions from "./positions";
import Draggable from "react-draggable";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { WARN_MSG_POS, WARN_MSG_SIZE } from "../constants";

export const EventBody = (props) => {
  const { moves, setMoves, actions, setActions, actions2, setActions2 } = props;

  const ref1 = React.useRef();
  const ref2 = React.useRef();

  const [sprite1, setSprite1] = React.useState(
    require("../Assets/images/cat.png")
  );
  const [sprite2, setSprite2] = React.useState(null);
  const [displayAddIcon, setDisplayAddIcon] = React.useState(true);
  const [positions, setPositions] = React.useState({
    r1: "0%",
    t1: "0%",
    scale1: 1,
    angle1: 0,
    r2: "0%",
    t2: "0%",
    scale2: 1,
    angle2: 0,
  });

  const checkCollision = () => {
    const rect1 = ref1.current.getBoundingClientRect();
    const rect2 = ref2.current.getBoundingClientRect();
    return (
      rect1.x < rect2.x + rect2.width &&
      rect1.x + rect1.width > rect2.x &&
      rect1.y < rect2.y + rect2.height &&
      rect1.y + rect1.height > rect2.y
    );
  };

  const swapAnimations = () => {
    setActions((prev) => [...actions2]);
    setActions2((prev) => [...actions]);
  };

  const updatePosition = (spriteRef, r, t, scale, angle) => {
    spriteRef.current.style.transform = `scale(${scale}) translate(${r}, ${t}) rotate(${angle}deg)`;
  };

  const moveSprite = (i, action, direction) => {
    setTimeout(() => {
      const spriteKey =
        direction === "x"
          ? action.includes("-")
            ? "r2"
            : "r1"
          : action.includes("-")
          ? "t2"
          : "t1";
      const offset =
        direction === "x"
          ? action.includes("-")
            ? -50
            : 50
          : action.includes("-")
          ? -50
          : 50;
      const newPos = parseInt(positions[spriteKey].slice(0, -1)) + offset;

      if (newPos < -290 || newPos > 290) {
        toast.warn(WARN_MSG_POS, { position: "top-center", autoClose: 2000 });
        return;
      }

      setPositions((prev) => ({ ...prev, [spriteKey]: `${newPos}%` }));
      updatePosition(
        direction === "x" ? ref1 : ref2,
        positions.r1,
        positions.t1,
        positions.scale1,
        positions.angle1
      );

      if (checkCollision()) {
        swapAnimations();
      }
    }, i * 1500);
  };

  const rotateSprite = (angle, action) => {
    setTimeout(() => {
      if (action === "sprite1") {
        setPositions((prev) => ({
          ...prev,
          angle1: prev.angle1 + angle,
        }));
        updatePosition(
          ref1,
          positions.r1,
          positions.t1,
          positions.scale1,
          positions.angle1 + angle
        );
      } else {
        setPositions((prev) => ({
          ...prev,
          angle2: prev.angle2 + angle,
        }));
        updatePosition(
          ref2,
          positions.r2,
          positions.t2,
          positions.scale2,
          positions.angle2 + angle
        );
      }
    }, 1500);
  };

  const startActions = (action, idx, isSprite1) => {
    switch (action) {
      case "move x by 50":
        moveSprite(idx, "move x by 50", "x");
        break;
      case "move y by 50":
        moveSprite(idx, "move y by 50", "y");
        break;
      case "move x by -50":
        moveSprite(idx, "move x by -50", "x");
        break;
      case "move y by -50":
        moveSprite(idx, "move y by -50", "y");
        break;
      case "rotate 45":
        rotateSprite(45, isSprite1 ? "sprite1" : "sprite2");
        break;
      case "rotate 90":
        rotateSprite(90, isSprite1 ? "sprite1" : "sprite2");
        break;
      case "random position":
        // Implement random position logic
        break;
      case "move (0, 0)":
        // Implement move to (0, 0) logic
        break;
      default:
        break;
    }
  };

  const refreshPositions = () => {
    setPositions({
      r1: "0%",
      t1: "0%",
      scale1: 1,
      angle1: 0,
      r2: "0%",
      t2: "0%",
      scale2: 1,
      angle2: 0,
    });
    updatePosition(ref1, "0%", "0%", 1, 0);
    updatePosition(ref2, "0%", "0%", 1, 0);
  };

  const runActions = (isSprite1) => {
    const actionList = isSprite1 ? actions : actions2;
    actionList.forEach((item, i) => {
      startActions(item.todo, i, isSprite1);
    });
  };

  return (
    <div className="mainContainer">
      <ToastContainer />
      <div
        className="playRefresh"
        style={{
          justifyContent: "flex-end",
          display: "flex",
          marginTop: "1rem",
        }}
      >
        <Button
          variant="contained"
          color="success"
          onClick={() => {
            runActions(true);
            runActions(false);
          }}
        >
          <PlayArrowIcon />
        </Button>
        <Button variant="contained" color="error" onClick={refreshPositions}>
          <RefreshIcon sx={{ color: "white" }} />
        </Button>
      </div>
      <div className="container">
        <Droppable droppableId="MovesList">
          {(provided) => (
            <div
              className="moves"
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <div className="moves__heading">Moves</div>
              {moves?.map((move, index) => (
                <SingleAction
                  disableDelete={true}
                  index={index}
                  moves={moves}
                  move={move}
                  key={move.id}
                  setMoves={setMoves}
                />
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>

        <Droppable droppableId="MovesActions">
          {(provided) => (
            <div
              className="moves actions"
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <span className="moves__heading">Action</span>
              {actions?.map((move, index) => (
                <SingleAction
                  index={index}
                  moves={actions}
                  move={move}
                  key={move.id}
                  refresh={refreshPositions}
                  setMoves={setActions}
                />
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>

        {displayAddIcon && (
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <AddBoxIcon
              sx={{ color: "gray", cursor: "pointer" }}
              onClick={() => {
                setDisplayAddIcon(false);
                setSprite2(require("../Assets/images/jerry1.png"));
                refreshPositions();
              }}
            />
            <DeleteIcon
              onClick={() => setActions([])}
              sx={{ cursor: "pointer", fontSize: "30px", color: "Grey" }}
            />
          </div>
        )}

        {!displayAddIcon && (
          <Droppable droppableId="MovesActions2">
            {(provided) => (
              <div
                className="moves actions"
                ref={provided.innerRef}
                {...provided.droppableProps}
              >
                <span className="moves__heading">Action 2</span>
                {actions2?.map((move, index) => (
                  <SingleAction
                    index={index}
                    moves={actions2}
                    move={move}
                    key={move.id}
                    refresh={refreshPositions}
                    setMoves={setActions2}
                  />
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        )}

        <div className="sprites">
          <Draggable
            bounds="parent"
            position={{ x: parseInt(positions.r1), y: parseInt(positions.t1) }}
            ref={ref1}
          >
            <img
              src={sprite1}
              style={{
                transform: `scale(${positions.scale1}) rotate(${positions.angle1}deg)`,
              }}
              alt="Sprite 1"
            />
          </Draggable>
          {sprite2 && (
            <Draggable
              bounds="parent"
              position={{
                x: parseInt(positions.r2),
                y: parseInt(positions.t2),
              }}
              ref={ref2}
            >
              <img
                src={sprite2}
                style={{
                  transform: `scale(${positions.scale2}) rotate(${positions.angle2}deg)`,
                }}
                alt="Sprite 2"
              />
            </Draggable>
          )}
        </div>
      </div>
    </div>
  );
};
