import * as React from "react";
import { SingleAction } from "./singleAction";
import { Droppable } from "react-beautiful-dnd";
import { Button } from "@mui/material";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import RefreshIcon from "@mui/icons-material/Refresh";
import AddBoxIcon from "@mui/icons-material/AddBox";
import DeleteIcon from "@mui/icons-material/Delete";
import DisabledByDefaultIcon from "@mui/icons-material/DisabledByDefault";
import { Sprites } from "./spriteProps";
import Positions from "./positons";
import Draggable1 from "react-draggable";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { WARN_MSG_POS, WARN_MSG_SIZE } from "../constants";

export const EventBody = (props) => {
  const { moves, setMoves, actions, setActions, setActions2, actions2 } = props;

  const ref = React.useRef();
  const ref2 = React.useRef();

  // Use state for position and transformations
  const [r, setR] = React.useState("0%");
  const [t, setT] = React.useState("0%");
  const [scale, setScale] = React.useState(1);
  const [angle, setAngle] = React.useState(0);

  const [r2, setR2] = React.useState("0%");
  const [t2, setT2] = React.useState("0%");
  const [scale2, setScale2] = React.useState(1);
  const [angle2, setAngle2] = React.useState(0);

  const [hello, setHello] = React.useState(false);
  const [hello2, setHello2] = React.useState(false);
  const [displayAddIcon, setDisplayAddIcon] = React.useState(true);
  const [sprite, setSprite] = React.useState(
    require("../Assets/images/cat.png")
  );
  const [sprite2, setSprite2] = React.useState(null);

  // Simplified transform function using React state
  function transform(temp, xAxis, action1) {
    let value = temp.toString();
    if (xAxis) {
      if (action1) {
        setR(value.concat("%"));
      } else {
        setR2(value.concat("%"));
      }
    } else {
      if (action1) {
        setT(value.concat("%"));
      } else {
        setT2(value.concat("%"));
      }
    }
  }

  // Move up example with state updates
  function moveUp(i, action1) {
    setTimeout(() => {
      let temp = parseInt(action1 ? t.slice(0, -1) : t2.slice(0, -1));
      temp = temp - 20;
      if (temp < -140) {
        refresh(WARN_MSG_POS);
        return;
      }
      transform(temp, false, action1);
      // Check for collision immediately after moving
      if (checkCollision()) {
        handleCollision(temp, action1);
        return; // Stop further actions if collision occurs
      }
    }, i * 1500);
  }

  // Other movement functions similar to moveUp

  // Same startActions logic, but now it will properly update the sprite's position
  const startActions = (action, idx, action1) => {
    switch (action) {
      case "move x by 50":
        moveRight(idx, action1);
        break;
      case "move y by 50":
        moveUp(idx, action1);
        break;
      // Add other cases here
      default:
        break;
    }
  };

  // Running actions
  function runAction1() {
    actions &&
      actions.map((item, i) => {
        startActions(item.todo, i, true);
        return null;
      });
  }

  function runAction2() {
    !displayAddIcon &&
      actions2 &&
      actions2.map((item, i) => {
        startActions(item.todo, i, false);
        return null;
      });
  }
  return (
    <div className="mainContainer">
      <ToastContainer />

      <div
        className="playRefresh"
        style={{
          justifyContent: "flex-end",
          display: "flex",
          marginTop: "1rem",
          marginBottom: "-2rem",
        }}
      >
        <Positions handleMove={moveXY} refresh={refresh} />

        <div className="gameProps" style={{ marginTop: "-0.5rem" }}>
          <Sprites
            setSprite={setSprite}
            setSprite2={setSprite2}
            displayAddIcon={displayAddIcon}
            sprite2={sprite2}
            sprite={sprite}
          />
        </div>
        <Button
          variant="contained"
          sx={{
            borderRadius: "20px",
            marginRight: "3px",
            height: "40px",
            width: "80px",
            marginTop: "2rem",
          }}
          color="success"
          onClick={() => {
            runAction1();
            runAction2();
          }}
        >
          <PlayArrowIcon />
        </Button>
        <Button
          variant="contained"
          sx={{
            borderRadius: "20px",
            height: "40px",
            width: "80px",
            marginTop: "2rem",
          }}
          color="error"
          onClick={refresh}
        >
          <RefreshIcon sx={{ color: "white" }} />
        </Button>
      </div>
      <div className="container">
        <Droppable droppableId="MovesList">
          {(provided) => (
            <div
              className="moves"
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <div className="moves__heading">Moves</div>
              {moves?.map((move, index) => (
                <SingleAction
                  disableDelete={true}
                  index={index}
                  moves={moves}
                  move={move}
                  key={move.id}
                  setMoves={setMoves}
                />
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>

        <Droppable droppableId="MovesActions">
          {(provided) => (
            <div
              className="moves actions"
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <span className="moves__heading">Actions</span>
              {actions?.map((move, index) => (
                <SingleAction
                  index={index}
                  moves={actions}
                  move={move}
                  key={move.id}
                  refresh={refresh}
                  setMoves={setActions}
                />
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>

        {displayAddIcon && (
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <div className="icon">
              <AddBoxIcon
                sx={{ color: "gray", cursor: "pointer" }}
                onClick={() => {
                  setDisplayAddIcon(!displayAddIcon);
                  setSprite2(require("../Assets/images/jerry1.png"));
                  refresh();
                }}
              />
              <span class="tooltiptext">Add</span>
            </div>
            <div>
              <DeleteIcon
                onClick={() => {
                  setActions([]);
                }}
                sx={{ cursor: "pointer", fontSize: "30px", color: "Grey" }}
              />
            </div>
          </div>
        )}
        {!displayAddIcon && (
          <Droppable droppableId="MovesActions2">
            {(provided) => (
              <div
                className="moves actions"
                ref={provided.innerRef}
                {...provided.droppableProps}
              >
                <span className="moves__heading">Action 2</span>
                {actions2?.map((move, index) => (
                  <SingleAction
                    index={index}
                    moves={actions2}
                    move={move}
                    key={move.id}
                    refresh={refresh}
                    setMoves={setActions2}
                  />
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        )}
        {!displayAddIcon && (
          <div className="icon">
            <DisabledByDefaultIcon
              sx={{ color: "gray", cursor: "pointer" }}
              onClick={() => {
                setDisplayAddIcon(!displayAddIcon);
                setSprite2(null);
                refresh();
              }}
            />
            <div>
              <DeleteIcon
                onClick={() => {
                  setActions([]);
                  setActions2([]);
                }}
                sx={{ cursor: "pointer", fontSize: "30px", color: "Grey" }}
              />
            </div>
          </div>
        )}

        <div
          className="moves play"
          style={{
            background: theme
              ? 'url("https://www.hp.com/us-en/shop/app/assets/images/uploads/prod/misty-forest-background1595620320482968.jpg?impolicy=prdimg&imdensity=1&imwidth=1000")'
              : null,
            backgroundSize: theme ? "100% 100%" : null,
          }}
        >
          <Draggable1
            bounds={{ left: -540, top: -250, right: 540, bottom: 250 }}
          >
            <div style={{ display: "flex", flexDirection: "row" }}>
              <div
                ref={ref}
                style={{
                  position: "relative",
                  transition: "1s all ease",
                }}
              >
                {hello ? (
                  <div
                    style={{ transition: "0s all ease" }}
                    className="msgPopup"
                  >
                    hello!
                  </div>
                ) : null}
                <img
                  src={sprite}
                  draggable="false"
                  style={{
                    cursor: "pointer",
                    position: "relative",
                    height: 200,
                    width: 200,
                    transition: "1s all ease",
                  }}
                />
              </div>
              {!displayAddIcon && (
                <div
                  ref={ref2}
                  style={{
                    position: "relative",
                    transition: "1s all ease",
                  }}
                >
                  {hello2 ? (
                    <div
                      style={{ transition: "0s all ease" }}
                      className="msgPopup"
                    >
                      hello!
                    </div>
                  ) : null}
                  <img
                    src={sprite2}
                    draggable="false"
                    style={{
                      cursor: "pointer",
                      position: "relative",
                      height: 200,
                      width: 200,
                      transition: "1s all ease",
                    }}
                  />
                </div>
              )}
            </div>
          </Draggable1>
        </div>
      </div>
    </div>
  );
};
export default EventBody;
